document.addEventListener('DOMContentLoaded', function() {
    // Quantity controls
    document.querySelectorAll('.quantity-control').forEach(control => {
        const minus = control.querySelector('.minus');
        const plus = control.querySelector('.plus');
        const quantity = control.querySelector('.quantity');
        
        minus.addEventListener('click', (e) => {
            e.preventDefault();
            let qty = parseInt(quantity.textContent);
            if (qty > 1) {
                quantity.textContent = qty - 1;
            }
        });
        
        plus.addEventListener('click', (e) => {
            e.preventDefault();
            let qty = parseInt(quantity.textContent);
            quantity.textContent = qty + 1;
        });
    });

    // Form handling
    const contactForm = document.getElementById('contactForm');
    if (contactForm) {
        contactForm.addEventListener('submit', (e) => {
            e.preventDefault();
            alert('Thank you! Your message has been sent.');
            e.target.reset();
        });
    }

    const serviceForm = document.getElementById('serviceForm');
    if (serviceForm) {
        serviceForm.addEventListener('submit', (e) => {
            e.preventDefault();
            alert('Service request received! We will contact you soon.');
            e.target.reset();
        });
    }
});